otype.formatTime=function(){ this.seconds = this.addLeadingZero(this.seconds); this.minutes = this.addLeadingZero(this.minutes); this.hours = this.addLeadingZero(this.hours); } CountUp.prototype.addLeadingZero=function(value){ return (value + &#34;&#34;).length &lt; 2 ? (&#34;0&#34; + value) : value; } CountUp.prototype.calculate=function(){ var currDate = new Date(); var prevDate = this.beginDate; this.seconds = this.datePartDiff(prevDate.getSeconds(), currDate.getSeconds(), 60); this.minutes = this.datePartDiff(prevDate.getMinutes(), currDate.getMinutes(), 60); this.hours = this.datePartDiff(prevDate.getHours(), currDate.getHours(), 24); this.days = this.datePartDiff(prevDate.getDate(), currDate.getDate(), this.numOfDays[currDate.getMonth()-1]); this.months = this.datePartDiff(prevDate.getMonth(), currDate.getMonth(), 12); this.years = this.datePartDiff(prevDate.getFullYear(), currDate.getFullYear(),0); } CountUp.prototype.update=function(id){ if ( ++this.seconds == 60 ) { this.seconds = 0; if ( ++this.minutes == 60 ) { this.minutes = 0; if ( ++this.hours == 24 ) { this.hours = 0; if ( ++this.days == this.numOfDays[(new Date()).getMonth()-1]){ this.days = 0; if ( ++this.months == 12 ) { this.months = 0; this.years++; } } } } } this.formatTime(); var countainer = document.getElementById(id); countainer.innerHTML =&#34;&lt;strong&gt;&#34; + this.years + &#34;&lt;/strong&gt; &lt;small&gt;years&lt;/small&gt; &lt;strong&gt;&#34; + this.months + &#34;&lt;/strong&gt; &lt;small&gt;months&lt;/small&gt;&lt;strong&gt; &#34; + this.days + &#34;&lt;/strong&gt; &lt;small&gt;days&lt;/small&gt; &lt;strong&gt;&#34; + this.hours + &#34;&lt;/strong&gt; &lt;small&gt;hours&lt;/small&gt; &lt;strong&gt;&#34; + this.minutes + &#34;&lt;/strong&gt; &lt;small&gt;minutes&lt;/small&gt; &lt;strong&gt;&#34; + this.seconds + &#34;&lt;/strong&gt; &lt;small&gt;seconds&lt;/small&gt;.&#34;; var self=this; setTimeout(function(){self.update(id);}, 1000); }
